//2.Now, solve the above question using assignment operators (eg. +=, -=, *=).


package javaAssignment;

public class que2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 2345;

        num += 8;    // Add 8 to num (2345 + 8 = 2353)
        num /= 3;    // Divide by 3 (2353 / 3 = 784)
        num %= 5;    // Modulus with 5 (784 % 5 = 4)
        num *= 5;    // Multiply by 5 (4 * 5 = 20)

        System.out.println("Final Result: " + num);
		

	}

}
